import React from "react";
import { NavLink } from "react-router-dom";

class Slider2 extends React.Component {
  componentDidMount() {
    function loadScript(src) {
      return new Promise(function (resolve, reject) {
        var script = document.createElement("script");
        script.src = src;
        script.addEventListener("load", function () {
          resolve();
        });
        script.addEventListener("error", function (e) {
          reject(e);
        });
        document.body.appendChild(script);
        document.body.removeChild(script);
      });
    }

    loadScript("./assets/js/rev-script-2.js");
  }
  constructor() {
    super();
    this.state = {
      isOpen: false,
    };
    this.openModal = this.openModal.bind(this);
  }

  openModal() {
    this.setState({ isOpen: true });
  }
  render() {
    return (
      <>
       <div className="relative w-full h-screen overflow-hidden">
      {/* Background Video */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover"
        autoPlay
        loop
        muted
        playsInline
      >
        <source
          src={require("./../../images/videos/large_view.mp4")}
          type="video/mp4"
        />
        Your browser does not support the video tag.
      </video>

      {/* Overlay */}
      <div className="absolute top-0 left-0 w-full h-full bg-black/50" />

      {/* Content */}
      <div className="relative z-10 flex flex-col items-center justify-center h-full text-center px-4 py-5">
        <h3 className="text-white text-xl md:text-2xl mb-4 font-light">
          Creative & Modern
        </h3>
        <h1 className="text-white text-4xl md:text-6xl font-extrabold uppercase mb-6">
          Website Design Company
        </h1>
        <p className="text-white max-w-2xl mb-8">
          We craft responsive, user-friendly, and visually stunning websites
          that help your business stand out and grow online.
        </p>
        <NavLink
          to="/about-1"
          className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-full shadow-lg transition"
        >
          Get Started
        </NavLink>
      </div>
    </div>
      </>
    );
  }
}

export default Slider2;
