import React from 'react';
import { NavLink } from 'react-router-dom';

class Slider2 extends React.Component {
    componentDidMount() {
        function loadScript(src) {

            return new Promise(function (resolve, reject) {
                var script = document.createElement('script');
                script.src = src;
                script.addEventListener('load', function () {
                    resolve();
                });
                script.addEventListener('error', function (e) {
                    reject(e);
                });
                document.body.appendChild(script);
                document.body.removeChild(script);
            })
        };

        loadScript('./assets/js/rev-script-2.js');

    };
    constructor() {
        super()
        this.state = {
            isOpen: false
        }
        this.openModal = this.openModal.bind(this)
    }

    openModal() {
        this.setState({ isOpen: true })
    }
    render() {
        return (
            <>
                <div id="rev_slider_one_wrapper" className="rev_slider_wrapper fullscreen-container" data-alias="mask-showcase" data-source="gallery" style={{ background: '#aaaaaa', padding: 0 }}>
                        {/* START REVOLUTION SLIDER 5.4.1 fullscreen mode */}
                        <div id="rev_slider_one" className="rev_slider fullscreenbanner" style={{ display: 'none' }} data-version="5.4.1">
                            <ul>
                                {/* SLIDE 1 */}
                                <li data-index="rs-70" data-transition="fade" data-slotamount="default" data-hideafterloop={0} data-hideslideonmobile="off" data-easein="default" data-easeout="default" data-masterspeed={300} data-thumb={require('./../../images/main-slider/slider2/slide3.jpg')} data-rotate={0} data-saveperformance="off" data-title data-param1={1} data-param2 data-param3 data-param4 data-param5 data-param6 data-param7 data-param8 data-param9 data-param10 data-description>
                                    {/* MAIN IMAGE */}
                                    <video 
  className="rev-slidebg" 
  autoPlay 
  loop 
  muted 
  playsInline 
  data-bgfit="cover" 
  data-bgposition="center center" 
  data-bgrepeat="no-repeat" 
  data-no-retina
>
  <source src={require('./../../images/videos/large_view.mp4')} type="video/mp4" />
  Your browser does not support the video tag.
</video>

                                   {/* LAYER 3  Thin text title */}
<div 
  className="tp-caption tp-resizeme slider-tag-line" 
  id="slide-70-layer-3"
  data-x="['center','center','center','center']"
  data-hoffset="['0','0','0','0']"
  data-y="['middle','middle','middle','middle']"
  data-voffset="['-80','-80','-80','-60']"
  data-textalign="['center','center','center','center']"
  style={{ zIndex: 10, color: '#fff', fontFamily: '"Poppins", sans-serif' }}
>
  Modern
</div>

{/* LAYER 4  Bold Title */}
<div 
  className="tp-caption tp-resizeme" 
  id="slide-70-layer-4"
  data-x="['center','center','center','center']"
  data-hoffset="['0','0','0','0']"
  data-y="['middle','middle','middle','middle']"
  data-voffset="['-10','-10','-10','0']"
  data-textalign="['center','center','center','center']"
  style={{ zIndex: 10, textTransform: 'uppercase', fontWeight: 800, color: '#fff', fontFamily: '"Poppins", sans-serif' }}
>
  Architecture
</div>

{/* LAYER 5  Paragraph */}
<div 
  className="tp-caption tp-resizeme" 
  id="slide-70-layer-5"
  data-x="['center','center','center','center']"
  data-hoffset="['0','0','0','0']"
  data-y="['middle','middle','middle','middle']"
  data-voffset="['60','60','60','50']"
  data-textalign="['center','center','center','center']"
  style={{ zIndex: 10, color: '#fff', fontFamily: '"Poppins", sans-serif' }}
>
  Analysis and planning services that help both the client and architects to work out the forthcoming project...
</div>

{/* LAYER 6  Button */}
<div 
  className="tp-caption rev-btn tp-resizeme" 
  id="slide-70-layer-6"
  data-x="['center','center','center','center']"
  data-hoffset="['0','0','0','0']"
  data-y="['middle','middle','middle','middle']"
  data-voffset="['140','140','140','120']"
  data-textalign="['center','center','center','center']"
>
  <NavLink to={"/about-1"} className="site-button btn-half"><span> Read More</span></NavLink>
</div>
 </li>
                                {/* SLIDE 2 */}
                               
                            </ul>
                            <div className="tp-bannertimer" style={{ height: 10, background: 'rgba(0, 0, 0, 0.15)' }} />
                        </div>
                    </div>
            </>
        );
    }
};

export default Slider2;
